// Format properties for WhatsApp
const properties = items[0].json;

if (!properties || properties.length === 0) {
  return [{
    json: {
      message: "I couldn't find properties matching your criteria. Try adjusting your budget or location.",
      hasProperties: false
    }
  }];
}

// Format property messages
let message = `ðŸŽ¯ Found ${properties.length} perfect matches!\n\n`;

properties.forEach((prop, index) => {
  const formattedPrice = new Intl.NumberFormat('en-NG', {
    style: 'currency',
    currency: 'NGN',
    maximumFractionDigits: 0
  }).format(prop.price);

  message += `${index + 1}. *${prop.type}*\n`;
  message += `ðŸ“ ${prop.location}\n`;
  message += `ðŸ’° ${formattedPrice}/year\n`;
  message += `âœ… ${prop.verified ? 'Verified' : 'Pending'}\n`;
  message += `ðŸ  Features: ${prop.features}\n`;
  message += `ðŸ”— View: propabridge.ng/${prop.id}\n\n`;
});

message += `Reply with property number for details or contact info.`;

return [{
  json: {
    message: message,
    hasProperties: true,
    properties: properties
  }
}];

// Handle non-search intents
const aiResponse = items[0].json;
let message = '';

switch(aiResponse.intent) {
  case 'list':
    message = `To list your property:\n\n`;
    message += `1. Send photos of your property\n`;
    message += `2. Share location and price\n`;
    message += `3. We'll verify within 24hrs\n\n`;
    message += `Or visit: propabridge.ng/list\n`;
    message += `Call: +234 805 526 9579`;
    break;

  case 'greeting':
    message = `ðŸ‘‹ Hi! I'm Propabridge AI.\n\n`;
    message += `Tell me what you're looking for:\n`;
    message += `â€¢ "3 bedroom in Wuse under 3M"\n`;
    message += `â€¢ "Apartments in Maitama"\n`;
    message += `â€¢ "Houses for sale in Abuja"\n\n`;
    message += `I'll find matches in 30 seconds!`;
    break;

  case 'question':
    message = aiResponse.response || "I can help you find or list properties. Just describe what you need!";
    break;

  default:
    message = aiResponse.response;
}

return [{
  json: {
    message: message,
    hasProperties: false
  }
}];

// Format analytics report
const data = items[0].json;

let report = `ðŸ“Š *Propabridge Daily Report*\n\n`;
report += `Date: ${new Date().toLocaleDateString()}\n\n`;

let totalConversations = 0;
let uniqueUsers = 0;
let intents = {};

data.forEach(row => {
  totalConversations += parseInt(row.total_conversations);
  uniqueUsers = Math.max(uniqueUsers, parseInt(row.unique_users));
  intents[row.intent] = (intents[row.intent] || 0) + parseInt(row.total_conversations);
});

report += `*Metrics:*\n`;
report += `â€¢ Total Conversations: ${totalConversations}\n`;
report += `â€¢ Unique Users: ${uniqueUsers}\n`;
report += `â€¢ Avg Conv/User: ${(totalConversations/uniqueUsers).toFixed(1)}\n\n`;

report += `*Intent Breakdown:*\n`;
Object.entries(intents).forEach(([intent, count]) => {
  const percentage = ((count/totalConversations) * 100).toFixed(0);
  report += `â€¢ ${intent}: ${count} (${percentage}%)\n`;
});

return [{
  json: {
    report: report,
    metrics: {
      totalConversations,
      uniqueUsers,
      intents
    }
  }
}];
